import React, {useState, useEffect} from 'react'
import axios from 'axios';

function Login() {
    //API fetching
    const [data, setData]=useState([])

    const fetchData = () => {
      fetch('http://localhost:4000/show')
        .then((res) => res.json())
        .then((response) => {
          console.log(response);
          setData(response);
        });
      }

      const [user,setUser]=useState({
        name:"",
        description:"",
        activity:"",
        duration:"",
        date:""
      })
      const updateinfo=(e)=>{
        setUser({ ...user, [e.target.name]: e.target.value });
      }

      const [edit, setEdit]=useState(false)
      const [editid,setEditid]=useState("")
      const onSubmit = (e) => {
        e.preventDefault();
        if (edit) {
          axios
            .put(`http://localhost:4000/update/${editid}`, infor)
            .then(res => {
              setEdit(false);
              setUser({
                name:"",
                description:"",
                activity:"",
                duration:"",
                date:""
              });
              fetchData();
            });
        } else {
          axios
            .post('http://localhost:4000/create', user)
            .then(res => {
                setUser({
                name:"",
                description:"",
                activity:"",
                duration:"",
                date:""
              });
              fetchData();
            });
        }
      };
      
      //Edit Data through _id
      const editData = id => {
        let newItem = data.find(elem => {
          return elem._id === id;
        });
        setEdit(true);
        setEditid(id);
        setUser({
          name: newItem.name,
          description: newItem.description,
          activity: newItem.activity,
          duration:newItem.duration,
          date:newItem.date
        });
      };


    // Delete Data
    const deleteUser = id => {
      axios
      .delete(`http://localhost:4000/delete/${id}`)
      .then(res => {
      fetchData();
    });
  };

//UseEffect
useEffect(()=>{
  fetchData();
}, [])

  return (
    <form className='lbimg' onSubmit={onSubmit}>
        <div className='container col-6 p-5'>
        <div>
            <label>Name</label>
            <input type="text" className="form-control" value={user.name} name="name" onChange={updateinfo}/>
        </div>

        <div>
            <label>Description</label>
            <input type="text" className='form-control' value={user.description} name="description" onChange={updateinfo}/>
        </div>

        <div>
            <label>Select Activity</label>
            <div className="mb-3">
            <select class="custom-select" id="inputGroupSelect04">
                <option selected>Choose...</option>
                <option value="1">Run</option>
                <option value="2">Bicycle Ride</option>
                <option value="3">Swim</option>
                <option value="4">Walk</option>
                <option value="5">Hike</option>
            </select>
            </div>
        </div>

        <div>
            <label>Duration</label>
            <input type="time" className='form-control' value={user.duration} name="duration" onChange={updateinfo}/>
        </div>

        <div>
            <label>Date</label>
            <input type="date" className='form-control' value={user.date} name="date" onChange={updateinfo}/>
        </div>
        </div>
    </form>
  )
}

export default Login
