import React from 'react'

function Login() {
    //API fetching
    const [data, setData]=useState([])

    const fetchData = () => {
      fetch('http://localhost:4000/show')
        .then((res) => res.json())
        .then((response) => {
          console.log(response);
          setData(response);
        });
      }

      const [infor,setInfor]=useState({
        name:"",
        email:"",
        city:""
      })
      const updateinfo=(e)=>{
        setInfor({ ...infor, [e.target.name]: e.target.value });
      }

      const [edit, setEdit]=useState(false)
      const [editid,setEditid]=useState("")
      const onSubmit = (e) => {
        e.preventDefault();
        if (edit) {
          axios
            .put(`http://localhost:4000/update/${editid}`, infor)
            .then(res => {
              setEdit(false);
              setInfor({
                name: '',
                email: '',
                city: ''
              });
              fetchData();
            });
        } else {
          axios
            .post('http://localhost:4000/create', infor)
            .then(res => {
              setInfor({
                name: '',
                email: '',
                city: ''
              });
              fetchData();
            });
        }
      };
      
      //Edit Data through _id
      const editData = id => {
        let newItem = data.find(elem => {
          return elem._id === id;
        });
        setEdit(true);
        setEditid(id);
        setInfor({
          name: newItem.name,
          email: newItem.email,
          city: newItem.city
        });
      };


    // Delete Data
    const deleteUser = id => {
      axios
      .delete(`http://localhost:4000/delete/${id}`)
      .then(res => {
      fetchData();
    });
  };

//UseEffect
useEffect(()=>{
  fetchData();
}, [])

  return (
    <form className='lbimg' onSubmit={onSubmit}>
        <div className='container col-6 p-5'>
        <div>
            <label>Name</label>
            <input type="text" className='form-control'/>
        </div>

        <div>
            <label>Description</label>
            <input type="text" className='form-control'/>
        </div>

        <div>
            <label>Select Activity</label>
            <div className="mb-3">
            <select class="custom-select" id="inputGroupSelect04">
                <option selected>Choose...</option>
                <option value="1">Run</option>
                <option value="2">Bicycle Ride</option>
                <option value="3">Swim</option>
                <option value="4">Walk</option>
                <option value="5">Hike</option>
            </select>
            </div>
        </div>

        <div>
            <label>Duration</label>
            <input type="time" className='form-control'/>
        </div>

        <div>
            <label>Date</label>
            <input type="date" className='form-control'/>
        </div>
        </div>
    </form>
  )
}

export default Login
